import '../App.css';
import React, {useContext} from 'react';
import Section from "./section";
import Stitched from "./Stitiched";
import {CategoriesContext} from "../Global/CategoriesContext";

function Categories(props) {
    const {Categories}= useContext(CategoriesContext);
    return (
        <div>
            <Section />
            <Stitched />

                {Categories.map((Categories) => (
                    <section className="container" key={Categories.id}>
                    <div className="row">
                        <div className="col-12 col-lg-6 col-xl-6 col-md-6">
                            <img src={Categories.image} className="img-fluid pt-5   " alt="..."/>
                            <h2 className="text-center pt-3 c">{Categories.name}</h2>
                            <a href="#" className="a aaa">SHOP NOW</a>
                        </div>
                    </div>
                    </section>
                ))}


        </div>
    )
}

export default Categories;
